import React, { useState, useRef } from 'react';
import './WorkBoard.css';
import Navbar from '../navbar/Navbar';
import ButtonMenu from '../button-menu/ButtonMenu';

const WorkBoard = () => {
    const [notes, setNotes] = useState<string[]>([]);
    const [draggedNote, setDraggedNote] = useState<number | null>(null);
    const [mousePosition, setMousePosition] = useState<{ x: number; y: number }>({ x: 0, y: 0 });

    const createNote = () => {
        // Создаем новую заметку
        const newNote = `Note ${notes.length + 1}`;

        // Добавляем новую заметку в массив
        setNotes([...notes, newNote]);
    };

    return (
        <div className='work-board'>
            <Navbar/>

            <div className='work-board__button'>
                <ButtonMenu
                    text="Создать заметку"
                    onClick={createNote}
                    fontSize='42px'
                />
            </div>

            {/* Отображаем заметки */}
            <div className='work-board__notes'>
                {notes.map((note, index) => (
                    <div key={index} className='work-board__note'>
                        {note}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default WorkBoard;
