import React, { useState, useRef } from 'react';
import './WorkBoard.css';
import Navbar from '../navbar/Navbar';
import ButtonMenu from '../button-menu/ButtonMenu';

const WorkBoard = () => {
    const [notes, setNotes] = useState<string[]>([]);
    const [draggedNote, setDraggedNote] = useState<number | null>(null);
    const [mousePosition, setMousePosition] = useState<{ x: number; y: number }>({ x: 0, y: 0 });
    const [newNotePosition, setNewNotePosition] = useState<{ x: number; y: number } | null>(null);

    const createNote = () => {
        // Создаем новую заметку
        const newNote = `Note ${notes.length + 1}`;

        // Добавляем новую заметку в массив
        setNotes([...notes, newNote]);

        // Устанавливаем позицию новой заметки над кнопкой
        const buttonPosition = document.querySelector('.work-board__button')?.getBoundingClientRect();
        if (buttonPosition) {
            setNewNotePosition({
                x: buttonPosition.left + buttonPosition.width / 2,
                y: buttonPosition.top - 20, // Выберите подходящее значение для вертикального смещения
            });
        }
    };

    const handleMouseDown = (index: number, event: React.MouseEvent) => {
        // Начинаем отслеживать перемещение при нажатии на заметку
        setDraggedNote(index);
        setMousePosition({ x: event.clientX, y: event.clientY });

        // Добавляем обработчики событий на весь документ для обработки перемещения за пределы заметки
        document.addEventListener('mousemove', handleMouseMove);
        document.addEventListener('mouseup', handleMouseUp);
    };

    const handleMouseMove = (event: MouseEvent) => {
        // Обновляем позицию мыши при движении
        setMousePosition({ x: event.clientX, y: event.clientY });
    };

    const handleMouseUp = () => {
        // Прекращаем отслеживать перемещение при отпускании мыши
        setDraggedNote(null);

        // Удаляем обработчики событий после отпускания мыши
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
    };

    return (
        <div className='work-board'>
            <Navbar />

            <div className='work-board__button'>
                <ButtonMenu text="Создать заметку" onClick={createNote} fontSize='42px' />
            </div>

            {/* Отображаем новую заметку */}
            {newNotePosition && (
                <div
                    className={`work-board__note ${newNotePosition ? 'dragged' : ''}`}
                    style={{
                        left: newNotePosition.x,
                        top: newNotePosition.y,
                        zIndex: 2,
                    }}
                >
                    Новая заметка
                </div>
            )}

            {/* Отображаем заметки */}
            <div className='work-board__notes'>
                {notes.map((note, index) => (
                    <div
                        key={index}
                        className={`work-board__note ${index === draggedNote ? 'dragged' : ''}`}
                        style={{
                            left: index === draggedNote ? mousePosition.x : 'auto',
                            top: index === draggedNote ? mousePosition.y : 'auto',
                            zIndex: index === draggedNote ? 2 : 1,
                        }}
                        onMouseDown={(event) => handleMouseDown(index, event)}
                    >
                        {note}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default WorkBoard;
